authorid = J.select('h1+span>a,h2 a').attr("href").match(/(\d+)/)[1];

des=String(J.select("span[style='color:#F98C4D']").text()).replace(/立意：/,'◎ ').replace(/一句话简介：/,'◎ ')+"◎"+java.getElement("id.novelintro").text();
