@js:
// Function to decompress GZIP data using Pako
function decompressGzip(data) {
    const compressedData = Uint8Array.from(atobPolyfill(data), c => c.charCodeAt(0));
    try {
        const decompressed = pako.ungzip(compressedData, { to: 'string' });
        return decompressed;
    } catch (error) {
        return "GZIP decompression failed: " + error;
    }
}

// Polyfill for atob, if your environment doesn't support it
function atobPolyfill(base64) {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=';
    let str = '';
    let buffer = 0;
    let bitsCollected = 0;

    for (let i = 0; i < base64.length; i++) {
        const char = base64.charAt(i);
        if (char === '=') {
            break;
        }
        const index = chars.indexOf(char);
        if (index === -1) {
            continue;
        }
        buffer = (buffer << 6) | index;
        bitsCollected += 6;
        if (bitsCollected >= 8) {
            bitsCollected -= 8;
            str += String.fromCharCode((buffer >> bitsCollected) & 0xff);
        }
    }
    return str;
}

// AES Base64 decode function
function aesBase64DecodeToString(input, key, iv) {
    let decodedData = CryptoJS.enc.Base64.parse(input);
    let keyWordArray = CryptoJS.enc.Utf8.parse(key);
    let ivWordArray = CryptoJS.enc.Utf8.parse(iv);
    try {
        let decrypted = CryptoJS.AES.decrypt(
            { ciphertext: decodedData },
            keyWordArray,
            {
                iv: ivWordArray,
                mode: CryptoJS.mode.CBC,
                padding: CryptoJS.pad.Pkcs7
            }
        );
        return decrypted.toString(CryptoJS.enc.Utf8);
    } catch (error) {
        return "AES decryption failed: " + error;
    }
}

// Function to decode the content directly
function decodeContent(result) {
    try {
        let text = aesBase64DecodeToString(result.substring(28), "KujiangApp747605", "5efd3f6060e20330");
        if (!text || text.startsWith("AES decryption failed")) {
            return text;  // Return if decryption fails
        }
        let decompressedText = decompressGzip(text);
        if (decompressedText.startsWith("GZIP decompression failed")) {
            return decompressedText;  // Return if decompression fails
        }
        return decompressedText;  // Return the final decompressed content
    } catch (error) {
        return "Error occurred: " + error;
    }
}
result =decodeContent(result)

