function decoderosiapng(dd) {
  // Step 1: Decode the input data
  let decodedData = CryptoJS.enc.Base64.parse(dd).toString(CryptoJS.enc.Utf8);;

  // Step 2: Calculate the offset and determine the start and end positions
  let offset = Number(decodedData.slice(8, 11));
  let start = offset + 11;
  let end = decodedData.length - offset;

  // Step 3: Check if start is less than end, to avoid invalid slicing
  if (start < end) {
    // Step 4: Process the data by replacing characters
    let processedData = decodedData
      .slice(start, end)
      .replace(/_/g, "8L3A+")
      .replace(/-/g, "PHA+");

    // Step 5: Decode the processed data
    return CryptoJS.enc.Base64.parse(processedData).toString(CryptoJS.enc.Utf8);;
  }

  // If the condition is not met, return null or handle it as needed
  return null;
}

