bookSourceComment:
function contentdecode(base64Str, key, iv) {
    // Decode the Base64 string
    const encryptedData = CryptoJS.enc.Base64.parse(base64Str);

    // Prepare the key and IV for AES decryption
    const keyHex = CryptoJS.enc.Utf8.parse(key); // Convert key to CryptoJS format
    const ivHex = CryptoJS.enc.Utf8.parse(iv);   // Convert IV to CryptoJS format

    // Decrypt the data
    const decryptedData = CryptoJS.AES.decrypt(
        { ciphertext: encryptedData },
        keyHex,
        { iv: ivHex, mode: CryptoJS.mode.CBC, padding: CryptoJS.pad.Pkcs7 }
    );

    // Convert decrypted data to string
    const decryptedString = decryptedData.toString(CryptoJS.enc.Utf8);

    return decryptedString;
}

