        "layout_flexGrow": 1,
        "layout_flexBasisPercent": size
    }
});
var baseUrl = "https://m.cqgmp.com/wap_leibie/1/";
var tag = java.ajax(baseUrl);
if (tag) {
    var a = org.jsoup.Jsoup.parse(tag).select("#smallnav a");
    push("🔥分类小说🔥", null, 1);
    for (var i in a) {
        var title = a[i].text();
        var url = a[i].attr("href");
        var size = 0.33;
        title = String(title).replace(/\s/g, "");
        url = String(url)+ "?page={{page}}";      
        push(title, url, size);
  }

} else {
  java.toast("🤔列表刷新失败！！！");     
} 
JSON.stringify(result);

