//Powered by RosiaPngå°çŒ«å’ª

function decrypt(a, b, c) {
    // Ensure key and IV are 16 bytes
    b = CryptoJS.enc.Utf8.parse(b.padStart(16, '0')); 
    c = CryptoJS.enc.Utf8.parse(c.padStart(16, '0')); 

    // Perform decryption
    let decrypted = CryptoJS.AES.decrypt(a, b, { 
        iv: c, 
        padding: CryptoJS.pad.Pkcs7 
    });

    return decrypted.toString(CryptoJS.enc.Utf8);
}

