function getKey(key) {
    const parts = key.split(";");
    for (let part of parts) {
        if (part.includes("secretKey2")) {
            return part.split("=")[1];
        }
    }
    return "";
}

function getArguments(open_argument,key,servers) {
    try {
        open_argument = JSON.parse(open_argument);
    } catch (e) {
        const serverList = servers.match(/https?:\/\/[^\s]+/g);
        open_argument = {
            "media": "小说",
            "tone_id": "默认音色",
            "server": serverList[0],
            "source":"全部"
        };
    }
    if (key) {return open_argument[key];} else {return open_argument}
}

function generateVideoPage(title, videoUrl) {
    return `<!DOCTYPE html><html lang="zh-CN"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>${title}</title><style>body,html{margin:0;padding:0;height:100%}video{width:100%;height:100%;object-fit:cover}.controls{position:absolute;bottom:20px;left:0;right:0;display:flex;justify-content:space-between;align-items:center;padding:0 20px;box-sizing:border-box;opacity:0;transition:opacity 0.3s ease}.controls.show{opacity:1}.progress-bar{flex:1;height:8px;background:rgba(255,255,255,0.2);border-radius:10px;position:relative;margin:0 15px;cursor:pointer}.progress{height:100%;background:linear-gradient(to right,#00c6ff,#0072ff);border-radius:10px}.btn{color:white;background:rgba(0,0,0,0.6);padding:8px 16px;border-radius:25px;cursor:pointer;font-size:16px;border:2px solid rgba(255,255,255,0.2);transition:background 0.3s ease}.btn:hover{background:rgba(0,0,0,0.8);border-color:white}.speed-btn{background:rgba(0,0,0,0.5);border-radius:20px;padding:8px 16px;color:white;font-size:14px;font-weight:bold;cursor:pointer;transition:background 0.3s ease}.speed-btn:hover{background:rgba(0,0,0,0.8)}</style></head><body><video id="video" controlslist="nodownload nofullscreen noremoteplayback" onclick="toggleControls(event)"><source src="${videoUrl}" type="video/mp4">您的浏览器不支持 HTML5 视频标签。</video><div class="controls" id="controls"><div class="btn play-pause" onclick="togglePlayPause()">⏸️</div><div class="progress-bar" onclick="seek(event)"><div class="progress"></div></div><div class="speed-btn" onclick="changeSpeed()">1x</div></div><script>const video=document.getElementById('video'),controls=document.getElementById('controls'),progressBar=document.querySelector('.progress-bar'),progress=document.querySelector('.progress'),speedBtn=document.querySelector('.speed-btn'),playPauseBtn=document.querySelector('.play-pause');video.onplay=updateProgress;video.ontimeupdate=updateProgress;function togglePlayPause(){if(video.paused){video.play();playPauseBtn.textContent='⏸️'}else{video.pause();playPauseBtn.textContent='▶️'}}function updateProgress(){const progressPercent=(video.currentTime/video.duration)*100;progress.style.width=\`\${progressPercent}%\`}function seek(event){const rect=progressBar.getBoundingClientRect(),clickPosition=(event.clientX-rect.left)/rect.width;video.currentTime=clickPosition*video.duration}function changeSpeed(){const currentSpeed=video.playbackRate;video.playbackRate=currentSpeed<3?currentSpeed+0.5:1;speedBtn.textContent=\`\${video.playbackRate}x\`}function toggleControls(event){if(event.target===video){controls.classList.toggle('show')}else{controls.classList.remove('show')}}window.onload=function(){video.play().catch(function(){console.log('自动播放被阻止')})}</script></body></html>`;
}

