exploreUrl:
@js:
sort = [];
push = (title, url, type) => sort.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
});
$ = (x) => `search?${x}&orderType=2&page={{page}}&searchType=1&size=40`;
u = source.key;
[["题材", "category"],
["标签", "includeTags"]].map([a, b] => {
  push(`・${a}・`, $(""), 1);
  d = /y/.test(b) ? "categories": "tags";
  c = JSON.parse(java.ajax(u + d)).model;
  for (i = 0; i < c.length; ++i) {
    push(
      c[i].name,
      $(b + "=" + encodeURIComponent(c[i].name)),
      null
    );
  };
});
push("・排行榜・", null, 1);
["周榜", "月榜", "年榜", "总榜"].map((p, q) => {
  push(p, `rank?type=${q + 1}`, null);
});
push("・状态・", null, 1);
["连载中", "已完结"].map((p, q) => {
  push(p, $(`finished=${q}`), null);
});
push("・篇幅・", null, 1);
["短篇", "中篇", "长篇"].map((p, q) => {
  push(p, $(`space=${q + 1}`), null);
});
push("・来源・", null, 1);
["原创首发", "会员上传"].map((p, q) => {
  push(p, $(`source=${q + 1}`), null);
});
JSON.stringify(sort);

