function register(is) {
  if (is == undefined) return true;
  info = result
  so =source.key
   pw = info.密码

    if (is) {
        info["用户名"] = `8x${parseInt(Math.random() * 1000000000000)}`;
        info["密码"] = "123456789";
        info["邮箱"] = `${parseInt(Math.random() * 1000000000000)}@163.com`;
    }

    url = so + (is? "register.php" : "login.php");
    body = `username=${info["用户名"]}&password=${info["密码"]}&repassword=${info["密码"]}&email=${info["邮箱"]}&sex=0&act=${is? "newuser" : "login"}&submit=&ajax_request=${Date.now()}`;

    result = java.post(url, body, { "Content-Type": "application/x-www-form-urlencoded" });
    cookie = result.cookies();
    ck = cookie? String(cookie).match(/\{(.*?)\}/)[1].replace(/,/g, ';') : "";

    if (!ck) {
        java.toast("注册失败，重新注册！");
        return false;
    }

    source.putLoginHeader(JSON.stringify({
        "Cookie": ck,
        "User-Agent": "Mozilla/5.0",
        "Referer": so
    }));
                source.putLoginInfo(JSON.stringify({
        "用户名": info["用户名"],
        "密码": info["密码"],
        "邮箱": info["邮箱"]
    }));
        //java.log(ck)
        //java.log(result.body())
        java.log(url + "," + JSON.stringify(body))
        java.toast(`注册成功----${info["用户名"]}`);
    return true;
}

function login(b) {
  if (b == undefined) return true;
  info = result
  so = source.key
  pw = info.密码

  if (b) {
    url = so + 'login.php,'
    body = `username=${info.用户名}&password=${pw}&submit=&act=login&usecookie=315360000`
  } else {
    url = so + 'register.php,'
    body = `username=${info.用户名}&password=${pw}&repassword=${pw}&email=${info.邮箱}&sex=0&act=newuser&Submit=&do=submit&ajax_request=${Date.now()}`
  }
  post = JSON.stringify({
    "body": String(body),
    "method": "POST"
  })
  url += post
  java.log(url)
  result = java.ajax(url)
  java.log(result)
  if(result.match(/出現錯誤/)){
  java.toast('\n'+'❗输入出错❗请重新输入👻')
  }else if(result.match(/用户名已注册/)){
  	java.toast('\n'+'👻用户名已注册👻')
  }else if(result.match(/source.key/)){
  	java.toast('\n'+info["用户名"]+'注册成功')
  	}else{
  java.toast('\n'+info["用户名"] + ' 登录成功')
  }
  c = cookie.getCookie(source.key)
  source.setVariable(c)
}
