function login() {
    let zdyserver;
    try {
        zdyserver = String(source.getLoginInfoMap()['服务器地址(可不填)']);
        if (zdyserver.includes('http')) {
            setArgument('server', zdyserver);
            java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
        }
    } catch (error) {
    }
    }


let server = getArgument('server');

function gets_key() {
    var servers = String(source.bookSourceUrl);
    const serverList = servers.split('\n');

    for (let server of serverList) {
        let key = String(cookie.getCookie(server)) || String(java.getCookie(server));
        if (key) {
            let keyParts = key.split('secretKey=');
            if (keyParts.length > 1) {
                key = keyParts[keyParts.length - 1];
                setArgument('key', key);
                return key;
            }
        }
    }

    return ''; 
}

// 填写密钥
function loginqt(){
    let key = gets_key()
    if (key && key != "") {
            java.toast("您已填写秘钥:"+key);
            return false
        }
    java.startBrowserAwait(getArgument('server')+'/check_key','登录大灰狼小说书源');
    key = gets_key()
    if (key){
        java.longToast('登录成功，秘钥：'+key);
        return true
        } else {
            java.toast('登录失败！！！！！');
            return false
            }
}

//登录番茄
function fq_login() {
    var cookies = cookie.getCookie('fanqienovel.com') || java.getCookie('fanqienovel.com')
    java.log(cookies)
    if (cookies && cookies != "") {
        java.toast("请不要重复登录，请先退出登录！")
        return false
    }
    try {
        java.startBrowserAwait("https://fanqienovel.com/", "登录")
    } catch (e) {
        java.toast(e)
    }
    try {
        cookie.removeCookie("snssdk.com")
    } catch (e) {}
    var cookies = cookie.getCookie("fanqienovel.com")
    if (!cookies || cookies == "") {
        java.toast("登录失败！")
        return false
    }
    java.toast("登录成功！")
    return true
}

//退出番茄
function logout() {
    cookie.removeCookie("fanqienovel.com");
    cookie.removeCookie("snssdk.com");
 //cookie.removeCookie(getArgument('server'));
    java.toast("退出登录成功");
}

//获取参数
function getArgument(key) {
    let open_argument = source.getVariable();
    open_argument = getArguments(open_argument,'');
    return open_argument[key];
}

//设置参数
function setArgument(key, value) {
    let open_argument = source.getVariable();
    open_argument = getArguments(open_argument,'');
    open_argument[key] = value;
    open_argument = JSON.stringify(open_argument);
    source.setVariable(open_argument);
    return open_argument;
}


function api() { 	java.startBrowserAwait(getArgument('server')+'/','首页');
    }
function hqmy() { 	java.startBrowserAwait(getArgument('server')+'/key','密钥');
    }
function vip() { 	java.startBrowserAwait(getArgument('server')+'/coffee',"喝咖啡");
    }
function clear() { 	java.startBrowserAwait(getArgument('server')+'/clear',"清空设备");
    }


function set(media) {
	  setArgument('media',media);
	  java.toast("\n\n已切换至："+media+'\n请重新搜索书籍！');
}


function getmedia() {
	   	let media = getArgument('media');
	   	if(media == '') {
	   		media = '全部来源都搜索';
	   		}
			  java.toast("\n\n当前搜索范围是："+media);

}

//获取服务器
function get_server() {
	  var server = getArgument('server');
    var tishi = '\n\n当前服务器：'+ server
    java.toast(tishi);
    return server
}


//设置服务器
function set_server() {
    let zdyserver;
    try {
        zdyserver = String(source.getLoginInfoMap()['服务器地址(可不填)']);
        if (zdyserver.includes('http')) {
            setArgument('server', zdyserver);
            java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
        } else {
            zdyserver = '';
        }
    } catch (error) {
        zdyserver = '';
    }
    ;
    if (!zdyserver) {
        const servers = String(source.bookSourceUrl).split('\n').map(s => s.trim()).filter(s => s && !s.includes('提示'));
        const currentServer = getArgument('server') || '';
        const currentIndex = servers.indexOf(currentServer);

        const nextIndex = currentIndex >= 0 ? (currentIndex + 1) % servers.length : 0;
        const nextServer = servers[nextIndex];

        setArgument('server', nextServer);
        java.toast(`\n\n当前服务器${nextIndex+1}:${nextServer}\n\n注意：切换服务器后需要在详情页刷新目录才能生效`);
    }
}

