ruleSearch.intro:
@js:
key = String(java.get('key'));
// str 即为input输入的数据
function sliceCountStr(str) {
   let arr = []; // 记录分割计算表达式
   for (let i = 0; i < str.length; i++) {
      let item = str.charAt(i);
      let num = item;
      if (/[\d|\.]/.test(item)) {
          let j = i + 1;
          for (; j < str.length - 1; j++) {
            let otherItem = str.charAt(j);
            if (!/[\d|\.]/.test(otherItem)) {
              break;
            }
          }
          num = str.slice(i, j);
          i = j - 1;
          num = +num;
      }
      arr.push(num);
   }
   return arr;
}
// arr 即为上一步处理好的数组
function countHandle(arr) {
      let charArr = [],
        numArr = [];
      for (let i = 0; i < arr.length; i++) {
        if (typeof arr[i] == 'number') {
          numArr.push(arr[i]);
        } else {
          if (charArr.length) {
            // 步骤1
            // 如果当前的运算符的优先级比栈顶的优先级低或相等，就说明需要把前面的值全部计算好
            // 存储运算符的栈要一直出栈，直到栈为空或当前的字符的优先级比栈顶的优先级高
            while (this.isPop(arr[i], charArr[charArr.length - 1])) {
              let t2 = numArr.pop();
              let t1 = numArr.pop();
              let char = charArr.pop();
              this.handleCalculation(numArr, t1, t2, char);
            }
            // 当前运算符为右括号
            if (arr[i] == ')') {
              // 取栈顶运算符
              let st = charArr[charArr.length - 1];
              // 步骤2
              // 遇到右括号也要一直出栈，直到遇到左括号
              while (st != '(') {
                let t1, t2;
                let char = charArr.pop();
                if (char != '(') {
                  t2 = numArr.pop();
                  t1 = numArr.pop();
                  this.handleCalculation(numArr, t1, t2, char);
                }
                st = char;
              }
            }
            // 运算符不为右括号
            if (arr[i] != ')') {
