        info["用户名"] = `8x${parseInt(Math.random() * 1000000000000)}`;
        info["密码"] = "123456789";
        info["邮箱"] = `${parseInt(Math.random() * 1000000000000)}@163.com`;
        info["QQ"] = "";
    }

    url = so + (is? "register.php" : "login.php");
    body = `username=${info["用户名"]}&password=${info["密码"]}&repassword=${info["密码"]}&email=${info["邮箱"]}&sex=0&qq=${info["QQ"]}&act=${is? "newuser" : "login"}&submit=&ajax_request=${Date.now()}&checkcode=${java.getVerificationCode(imgurl)}`;

    result = java.post(url, body, { "Content-Type": "application/x-www-form-urlencoded" });
    cookie = result.cookies();
    ck = cookie? String(cookie).match(/\{(.*?)\}/)[1].replace(/,/g, ';') : "";

    if (!ck) {
        java.toast("注册失败，重新注册！");
        return false;
    }

  source.putLoginHeader(JSON.stringify({
        "Cookie": ck,
        "User-Agent": "Mozilla/5.0",
        "Referer": so
