@js:
sort=[];
burl=source.getKey().replace('//s','//book');
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});

	push("❤推荐", null, 1, 1);
tj = 	["每日优选","高分好书","真香新书","爆肝书单","宝藏好书","猜你喜欢"];
	tj.map((title,type)=>{
	url = burl + `/book_city/v7_more/index/1/20/${type+1}/{{page}}.html`
		push(title, url, 1, 0.25);										
			});


	eval(String(source.bookSourceComment));
	fl_url = burl + "/classify/v4/index.html";
	fl_content = JSON.parse(java.ajax(fl_url)).data.content;	
  fl = JSON.parse(decode(fl_content));

	[
	 ["❤男频-分类","1",fl.male],
		["❤女频-分类","2",fl.female],
	].map([title,gender,list]=>{		
		push(title, null, 1, 1);
			list.map(($,index)=>{	   	    

 	    ltype_name = $.ltype_name;
 	    ltype_id=$.ltype_id;
 	    ltype_url = burl + `/classify/v4/all/${gender}/${ltype_id}/-1/-1/-1/{{page}}.html`; 										
 	    push("@"+ltype_name+"@", ltype_url, 1, 1);

     	D=$.ltype_list;
   	D.map(($,index)=>{	   	     	    
   		 title = $.stype_name;
     	stype_id=$.stype_id;

     url = burl + `/classify/v4/all/${gender}/${ltype_id}/${stype_id}/-1/-1/{{page}}.html`; 											  
     	if(D.length > 3&&D.length!=4){
		if(index+1 <= D.length - D.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.29);}
				}
				else if(D.length == 4){ push(title, url, 1, 0.4);}
				else{ push(title, url, 1, 0.25);}
        });  
     });
  });


JSON.stringify(sort);

