	if (msg) throw new Error(msg)
	user = JSON.parse(java.ajax("https://api.sfacg.com/user")).data.nickName
	java.toast(user+"，登陆成功！")
	header = {
		"Cookie": java.getCookie(loginUrl)
	}
	return JSON.stringify(header)
}
//写入登录头
source.putLoginHeader(login())

