        typeof callback == "function" && callback(item, index, arr);
      }
    } catch (err) {
      java.log(`${index}  =é”™è¯¯=  ${item}\n${err}\n\n`);
    }
    // java.log(item + "\n\n")
    return arr
  }, [])
}

