const BASE64_CHARS = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=';

// è§£ç UTF-8ç¼–ç çš„å­—ç¬¦ä¸²
function decodeUtf8(str) {
  let result = '';
  for (let i = 0; i < str.length; i++) {
    let charCode = str.charCodeAt(i);
    if (charCode <= 0x7f) {
      result += String.fromCharCode(charCode);
    } else if (charCode <= 0xdf) {
      let nextCharCode = str.charCodeAt(++i);
      result += String.fromCharCode((charCode & 0x1f) << 0x6 | nextCharCode & 0x3f);
    } else {
      let nextCharCode1 = str.charCodeAt(++i);
      let nextCharCode2 = str.charCodeAt(++i);
      result += String.fromCharCode((charCode & 0xf) << 0xc | (nextCharCode1 & 0x3f) << 0x6 | nextCharCode2 & 0x3f);
    }
  }
  return result;
}

// è§£ç Base64ç¼–ç çš„å­—ç¬¦ä¸²
function decodeBase64(str) {
  let result = '';
  let i = 0;
  while (i < str.length) {
    let charCode1 = BASE64_CHARS.indexOf(str.charAt(i++));
    let charCode2 = BASE64_CHARS.indexOf(str.charAt(i++));
    let charCode3 = BASE64_CHARS.indexOf(str.charAt(i++));
    let charCode4 = BASE64_CHARS.indexOf(str.charAt(i++));

    let byte1 = (charCode1 << 2) | (charCode2 >> 4);
    let byte2 = ((charCode2 & 0xf) << 4) | (charCode3 >> 2);
    let byte3 = ((charCode3 & 0x3) << 6) | charCode4;

    result += String.fromCharCode(byte1);
    if (charCode3 !== 64) { // '='
      result += String.fromCharCode(byte2);
    }
    if (charCode4 !== 64) { // '='
      result += String.fromCharCode(byte3);
    }
  }
  return decodeUtf8(result);
}

// å°†ç‰¹å®šæ ¼å¼çš„å­—ç¬¦ä¸²è§£ç å¹¶æ˜¾ç¤ºåœ¨é¡µé¢å…ƒç´ ä¸­
function DecodedText(encodedStr) {
  const startIdx = parseInt(encodedStr.slice(4, 6), 10) + 6;
  const endIdx = encodedStr.length - parseInt(encodedStr.slice(4, 6), 10);
  const substring = encodedStr.slice(startIdx, endIdx);

  const base64Str = substring.replace(/_/g, '8L3A+').replace(/-/g, 'PHA+');
  return decodeBase64(base64Str)
}
