ruleToc.nextTocUrl:
@js:
var num = Math.floor(java.get("total") / 500); 
var res = String(baseUrl).match(/(.*offset=)\d+(.*)/) || ['',''];
var data = [];
for(i = 1;i <= num; i++){
    data.push(res[1] + (i*500) + res[2]);
}
data;

ruleToc.preUpdateJs:
if (!String(book.bookUrl).includes(Get('url'))) {
    book.bookUrl = String(book.bookUrl).replace(/.*\/\/[^\/]+/, Get('url'));
    book.tocUrl = String(book.tocUrl).replace(/.*\/\/[^\/]+/, Get('url'));
    java.refreshTocUrl();
}

