ruleContent.content:
@js:
let url = java.getElements('#mui-player').attr('src');
let img = java.getElements('#mui-player').attr('poster');
let name = String(java.getString('h1@text'));
let intro = String(java.getString('.detail_box@.item@html'));
let kind = java.getString('.tag_box@text');

let pic = `<img src="${img}" style="max-width: 100%; height: auto; border-radius: 8px;">`;

if (book.durChapterIndex === chapter.index) {
    let b64 = java.base64Encode(`
        <html>
            <head>
                <meta name="viewport" content="width=device-width">
                <style>
                    body {
                        margin: 0;
                        font-family: Arial, sans-serif;
                        background: linear-gradient(135deg, #ece9e6, #ffffff);
                        display: flex;
                        flex-direction: column;
                        align-items: center;
                        justify-content: flex-start;
                        padding: 20px;
                    }
                    .container {
                        width: 100%;
                        max-width: 800px;
                        background: #fff;
                        border-radius: 10px;
                        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                        overflow: hidden;
                        margin-bottom: 20px;
                    }
                    h3 {
                        margin: 0;
                        padding: 10px 20px;
                        font-size: 0.9em;
                        background: #333;
                        color: #fff;
                        border-radius: 10px 10px 0 0;
                    }
                    .video-container {
                        width: 100%;
                        background: #000;
                        position: relative;
                    }
                    video {
                        width: 100%;
                        height: auto;
                        display: block;
                    }
                    .info-container {
                        padding: 20px;
                    }
                    .info-container p {
                        margin: 10px 0;
                        color: #555;
                    }
                    .info-container p span {
                        font-weight: bold;
                        color: #000;
                    }
                </style>
            </head>
            <body>
                <div class="container">
                    <h3>${name}</h3>
                    <div class="video-container">
                        <video controls autoplay name="media">
                            <source src="${url}" type="video/mp4">
                        </video>
                    </div>
                    <div class="info-container">
                        <p>${intro}</p>
                        <p>标签：<span>${kind}</span></p>
                    </div>
                </div>
            </body>
        </html>`);

    let dataUrl = `data:text/html;base64,${b64}`;
    java.startBrowser(dataUrl, title);
    java.toast('正在加载视频\n视频加载较慢，请耐心等待');
}

'【刷新正文】播放\n✅直链:' + url + '\n' + pic;

