ruleContent.content:
$.posts[*].vod_content
<js>
const str = result.match(/images":([^}]+)/)[1];
const imageUrls = JSON.parse(str); 
let json = [];
for (url of imageUrls) {
  json.push({
    index: `<img src="${url}">`
  });
}
JSON.stringify(json)
</js>
