jsLib:
function _init(condition, callback) {
  let {
    java
  } = this;
  const separator = "<-separator->"
  let __next_f_text = this.src.match(/__next_f\.push\(\[1,\s*".+?"\]\)/gi).map(item => item = item.match(/__next_f\.push\(\[1,\s*"(.+?)"\]\)/)[1]).join("").replace(/^[0-9a-zA-Z]+:\w{0,3}/g, ``).replace(/\\"/g, `"`).replace(/(?:\\n)?(?:\w+:)(?:(?:(?:\w{0,3})(\[|\{|"\$))|(null))/g, `${separator}$1$2`);
  //java.log(__next_f_text+"\n\n======");

  return __next_f_text.split(separator).reduce((arr, item, index) => {
    item = item.replace(/\\n$/, "");
    try {
      //java.log(`${index}  ${item}\n\n`);
      item = item == "null" ? [] : JSON.parse(item);
    } catch (err) {
      // java.log(`${index}  错误  ${item}\n${err}\n\n`);
      item = [];
    }
    try {
      // java.log(`${index}  ${JSON.stringify(item)}\n\n`)
      if (condition && eval(condition)) {
        typeof callback == "function" && callback(item, index, arr);
      }
    } catch (err) {
      java.log(`${index}  =错误=  ${item}\n${err}\n\n`);
    }
    // java.log(item + "\n\n")
    return arr
  }, [])
}

function jsonpath(json) {
  return com.jayway.jsonpath.JsonPath.parse(JSON.stringify(json))
}

