function login() {
  let une = source.getLoginInfoMap().get("账号")
  let pwd = source.getLoginInfoMap().get("密码")
  if (une && pwd) {
    let body = String('username=' + une + '&password=' + pwd + '&usecookie=86400&act=login')
    let url = source.bookSourceUrl + '/login.php?do=submit'
    let res = java.post(url, body, {
      "Content-Type": "application/x-www-form-urlencoded"
    })
    let ck = res.cookies()
    if (res.body().match(/错误/gi)) {
      throw(res.body().match(/<div class="aui-ver-form">[\s\S]+<br>/gi)[0].replace(/<.+>\s*/gi,""))
    }
    let header = JSON.stringify({
      "Cookie": String(ck).match(/\{(.*?)\}/)[1].replace(/,/g, ';')
    })
    source.putLoginHeader(header)
  }
}

