X = J.select('.menu a');
for (i = 0; i < X.length; ++i) {
  push(
    X[i].text(),
    X[i].attr('href') + "<,index_{{page}}.html>",
    null
  );
};
push("・标签・", null, 1);
Y = J.select('.panel-default a');
for (i = 0; i < Y.length; ++i) {
  push(
    Y[i].text(),
    String(Y[i].attr('href')).replace(/\d+\.html$/,'{{page-1}}.html'),
    null
  );
};
push("・书单・", null, 1);
K = org.jsoup.Jsoup.parse(java.ajax(u + "/shudan"));
Z = K.select('.panel-warning .list-group-item a');
for (i = 0; i < Z.length; ++i) {
  push(
    Z[i].text(),
    Z[i].attr('href'),
    null
  );
};
p = K.select('.m-page .disabled').text().match(/\/(\d+)/)[1];
for (i = 2; i <= p; ++i) {
  M = org.jsoup.Jsoup.parse(java.ajax(u + `/shudan/index_${i}.html`));
  N = M.select('.panel-warning .list-group-item a');
  for (o = 0; o < N.length; ++o) {
