// 读取并返回数据
function checkData(_) {
	const { java, source, cache }= _ || this
	java.log("checkData()")
	const vName = `sourceVariable_${source.getKey()}`
	let temp
	try{
		temp = []
		// 尝试从缓存中分段读取数据
		for(let i = 0; i < cache.get(`${vName}_iLength`);){
			temp.push(cache.get(`${vName}_${i}`))
			i += 500000
		}
		temp = temp.join("")
		// 如果没有数据，尝试从缓存文件或源变量中获取
		if (!temp||temp=="") {
			temp=cache.getFile(vName)||cache.get(vName)
		}
		// 解析 JSON 数据
		const $=JSON.parse(temp)
		// 验证数据结构
		if (typeof $.books!='object') {
			throw new Error("错误数据")
		}
		return $
	} catch (e) {
		java.log(`function checkData Error:${e}`)
		return reset(false, false, this)
	}
}
// 保存数据
