function login() {
	info = JSON.parse(source.getLoginInfo())
	sum=(Number(info["获取数目"])||50)
	concurrency=(Number(info["并发数"])||1)
	data=checkData()
	data['sum']=sum
	data['concurrency']=concurrency
	setv(data,this)
	setInfo('获取数目',sum)
	setInfo('并发数',concurrency)
}
function setData(key,value,_){
	data=_||checkData(this)
	data[key]=value
	if (key=='sortType'){
		data['order']=!data.order
		setv(data,this)
		return data
		}
	setv(data,this)
	return data[key]
}
function setInfo(key,value){
	let data=JSON.parse(source.getLoginInfo()||"{}")
	data[key]=String(value)
	putLoginInfo(JSON.stringify(data))
	}
function putLoginInfo(info){
	try {
		let key=java.androidId()
		let encodeStr=Packages.android.util.Base64.encodeToString(java.createSymmetricCrypto("AES", key).encrypt(info),2)
		cache.put(`userInfo_${source.getKey()}`, encodeStr)
		return true
		}catch(e){
			java.log(e)
			return source.putLoginInfo(info)
		}
	}
function saveProgress(progress,notshow){
	source.put("progress",progress)
	if(!notshow){java.toast(progress)}
	}
function setLocal(times){
	source.put('isEnd',0)
	saveProgress(`\n⌛开始${times?("第"+times+"次"):""}构造本地数据⏳`,times)
	error=false
time = +java.timeFormatUTC(new Date(), 'yyyyMMdd', 8);
$=checkData()
if ((+$.time||0)<time){
	try{
	sleep(1000)
	saveProgress("\n🚀正在获取最新书籍🆔")
	result=org.jsoup.Jsoup.parse(java.ajax(source.key))
	hrefs=result.select(".chapter-new li a.name")
	hrefs.sort((a,b)=>b.attr("href").match(/\d+/)-a.attr("href").match(/\d+/))
	lastestId=Number(hrefs.attr("href").match(/\d+/)[0])
	$.time=time
	$.lastestId=lastestId
	}catch(e){
		java.log(e)
		error=true
		saveProgress("\n⚠️获取最新书籍🆔失败，请尝试切换网络")
		sleep(800)
		}
	}
	urls=[]
	for (let i=$.lastestId;i>$.lastestId-$.sum;i--){
		if (((i in $.books)&&$.books[i].tags)||i<1||(($.errorIds?$.errorIds:$.errorIds=[])&&$.errorIds.includes(i)))continue
		urls.push(`${source.key}/book/${i}/`)
		}
	sleep(1000)
	saveProgress(`\n当前最新书籍🆔为${$.lastestId}\n🚀正在并发获取${urls.length}本书籍🚀`)
	sleep(800)
	count=0
	urlslist=spArr(urls,$.concurrency||1)
	for (let i in urlslist){
		if (Number(source.get('isEnd'))){
			sleep(800)
			saveProgress(`\n❎用户终止在${count}/${urls.length}❎`)
			error=false
			sleep(800)
			break
			}
		try{
		 htmlList=java.ajaxAll(urlslist[i])
		 htmlList.forEach(h=>{
		 	html=h.body()
			let e=org.jsoup.Jsoup.parse(html)
			if(html==""||html.match(/<h1>404 Not Found/)){$.errorIds.push(Number(urls[i].match(/\d+/)))}
			id=String(e.select('[property="og:novel:read_url"]').attr("content")).match(/book\/(\d+)/)[1]
			$.books[id]={author:e.select('[property="og:novel:author"]').attr("content"),category:Object.values(e.select(".categories a").textNodes()),cover:String(e.select('[property="og:image"]').attr("content")).replace(/.*file\/cover\//,""),intro:e.select('.book-desc').textNodes().toArray().join(""),name:e.select('[property="og:novel:book_name"]').attr("content"),pornrate:e.select('.pornrate dd').text(),score:Number(e.select('.score dd').text()),status:e.select('[property="og:novel:status"]').attr("content"),tags:Object.values(e.select('.tag a').textNodes()),update:String(e.select('.new dd').textNodes()[0]).trim(),word:e.select(".wordcount dd").text()}
			count++
			progress=`\n${count%2?"⏳":"⌛"}当前进度(${count}/${urls.length})\n${'﹦'.repeat(Math.floor((count/urls.length)*39))}${'﹣'.repeat(Math.ceil((urls.length-count)*39/urls.length))}`
			saveProgress(progress,true)
			time=new Date()
			if(count==1||count==(urls.length)||time-pretime>15000){
			pretime=new Date()
			java.toast(progress)
			}
			})
			sleep(parseInt((Math.random()+0.65)*1000))

			}catch(e){
				java.log(e)
				error=true
				saveProgress(`\n❎终止在${count}/${urls.length}`)
				sleep(1000)
				break
				}
		}
	source.put("progress","")
	java.longToast(`\n✅构造本地数据完成，新增${count}本`)
	if(count>0){setv($)}
	if(error&&!Number(source.get('isEnd'))&&((times||0)<3)){
		sleep(2000)
		progress=`\n🔄开始第${(times||0)+1}次重试`
		saveProgress(progress)
		sleep(1000)
		setLocal((times||0)+1)
		}
}
function sN(str){
	return String(str).replace(/(\d)/g,`$1⃣️`)
	}
function exportData(t) {
	try{
		saveProgress("\n✅开始导出数据")
		sleep(800)
		v=JSON.stringify(checkData())
		body=v
		if (Number(source.get('isEncode'))){
			body=String(v).replace(/[\u4e00-\u9fa5]+/g, (match)=>match.replace(/[\u4e00-\u9fa5]/g,(char)=>'\\u' + ('0000' + char.charCodeAt(0).toString(16)).slice(-4)))
		}
		if (t) {
			java.toast("\n⚠️网络导出最大为10M，超过10M将导出失败")
			resp=java.post('https://jsonblob.com/api/jsonBlob',body, {"Content-Type":"application/json","Accept": "application/json"})
			path=String(resp.header("Location")).replace(/http:/,"https:")
		} else {
			m=String(JSON.parse(source.getLoginInfo()||'{}').数据地址).match(/(data\/[^\/]+\/cache)?\/?([^\/]+\.json)/)
			if(m){
				java.deleteFile("/"+m[m.length-1])
			}
			url=`data:;${java.md5Encode16(v)},{"type": "json"}`
			content=java.hexEncodeToString(body)
			path=java.downloadFile(content,url)
		}
		setInfo("数据地址",path)
		saveProgress(`\n✅导出成功✅\n${t?'地址为':'保存在阅读缓存路径\n/android/data/{package}/cache'}${path}`)
	} catch (e) {
		java.log(e)
		saveProgress("\n❎导出失败❎\n错误信息已打印在日志")
	}
}
function importData(url) {
	try{
		if(!(String(url||"").trim())) {
			url="https://jsonblob.com/api/jsonBlob/1334825983680897024"
		}
		setInfo("数据地址",url)
		saveProgress("\n✅开始导入数据")
		sleep(800)
		url = String(url)
		isDelete = false
		if (url.startsWith("http")) {
			path = java.downloadFile(url)
			isDelete = true
		}else {
			m = url.match(/(data\/[^\/]+\/cache)?\/?([^\/]+\.json)/)
			path = "/"+m[m.length-1]
		}
		data = java.readTxtFile(path)
		data = /base64Data/.test(data)?JSON.parse(data).base64Data:data
		data = /^[A-Za-z0-9+/]+={0,2}$/.test(data)?java.base64Decode(data):data
		data = JSON.parse(data)
		saveProgress('\n✅数据数目:'+Object.keys(data.books).length)
		setv(data)
		setInfo('获取数目',data.sum)
		setInfo('并发数',data.concurrency||1)
		sleep(2000)
		if (isDelete) {
			java.deleteFile(path)
		}
		saveProgress("\n✅导入成功✅")
	}catch (e) {
		java.log(e)
		saveProgress("\n❎导入失败❎\n错误信息已打印在日志")
	}
}

