function host(){
    var d="番茄",s=this.source;
    try{
        var u=(p=>p?Object.values(p[0])[0]:d)(s.getVariable()&&JSON.parse(s.getVariable()))||d;
        var result = u.replace(/\/$/,"");
        return "@" + result;
    }catch(e){
        var result = d.replace(/\/$/,"");
        return "@" + result;
    }
}

