 let all = source.getVariable();
 let list = String(all);
 let hm = ""
 let cc = java.base64Decode(hm);
 let html = String(cc).replace(/◎参考标识◎/,`list = ${list}`);
 //java.log(html);
return java.base64Encode(html);;
}

