function openUrl(encryptedBase64,numBase64) {
    const encryptedData = CryptoJS.enc.Base64.parse(encryptedBase64);
    const iv = CryptoJS.lib.WordArray.create(encryptedData.words.slice(0, 4), 16);
    const ciphertext = CryptoJS.lib.WordArray.create(encryptedData.words.slice(4), encryptedData.sigBytes - 16);

    // ç”Ÿæˆå¯†é’¥ï¼ˆä¸uaå…³è”ï¼Œè¦ä¸€è‡´ï¼‰
    const userAgent = "mozilla/5.0 (linux; android 9) mobile safari/537.36"
    const num = numBase64; 
    const numStr = CryptoJS.enc.Utf8.stringify(CryptoJS.enc.Base64.parse(num.toString()));
    const key = CryptoJS.MD5(userAgent + numStr).toString();
    const keyBytes = CryptoJS.enc.Utf8.parse(key);

    const decrypted = CryptoJS.AES.decrypt(
        { ciphertext: ciphertext },
        keyBytes,
        { iv: iv, mode: CryptoJS.mode.CBC, padding: CryptoJS.pad.Pkcs7 }
    );
    return decrypted.toString(CryptoJS.enc.Utf8);
}
