//java.toast(url);
let getbook =JSON.parse(java.ajax(url)).books;
let is_bookshelf;
let data = [];
if (book.order < 0 && !getbook.length) {
	// 在阅读书架，不在大灰狼书架，同步到大灰狼书架
    book_detail.is_bookshelf = true;
    book_detail.last_chapter_title = '未开始';
    book_detail.last_chapter_update_time = '未开始';
    data.push(book_detail);
} else if ( book.order < 0 && getbook.length) {
	// 在阅读书架，在大灰狼书架
	  var books=getbook[0];
    books.is_bookshelf = true;
    data.push(books);
} else if (book.order >= 0 && !getbook.length) {
	// 不在阅读书架，不在大灰狼书架，留下浏览记录
	   book_detail.is_bookshelf = false;
    book_detail.last_chapter_title = '未开始';
    book_detail.last_chapter_update_time = '未开始';
    data.push(book_detail);
	} else if (book.order >= 0 && getbook.length) {
	// 不在阅读书架，在大灰狼书架，大灰狼书架下架
	   var books=getbook[0];
    books.is_bookshelf = false;
    //data.push(books);
	}
if (data.length) {
	var option = {
"method": "POST",
"body": JSON.stringify(data),
};
    java.ajax(`${base_url}/push_user_bookshelf,`+JSON.stringify(option));
    }
	} catch (e) {}
	result;
</js>

