(video.paused){video.play();playPauseBtn.textContent='â¸ï¸'}else{video.pause();playPauseBtn.textContent='â–¶ï¸'}}function updateProgress(){const progressPercent=(video.currentTime/video.duration)*100;progress.style.width=\`\${progressPercent}%\`}function seek(event){const rect=progressBar.getBoundingClientRect(),clickPosition=(event.clientX-rect.left)/rect.width;video.currentTime=clickPosition*video.duration}function changeSpeed(){const currentSpeed=video.playbackRate;video.playbackRate=currentSpeed<3?currentSpeed+0.5:1;speedBtn.textContent=\`\${video.playbackRate}x\`}function toggleControls(event){if(event.target===video){controls.classList.toggle('show')}else{controls.classList.remove('show')}}window.onload=function(){video.play().catch(function(){console.log('è‡ªåŠ¨æ’­æ”¾è¢«é˜»æ­¢')})}</script></body></html>`;
