	let u_a=this.java.getWebViewUA();
	let headers = {
		  	'User-Agent': u_a,
		  'sec-ch-ua': s_c_ua,
    	 'sec-ch-ua-mobile': s_c_ua_m,
     	'sec-ch-ua-platform': s_c_ua_p,
     	'cache-control': cache_c,
     	'accept-language': a_l
