ruleContent.replaceRegex:
@js:
m=[];n=[];
function x(a,b){m.push(a);n.push(b)}
x(/{{chapter.title}}/g,"");
x(/除非注明，文章均由本站整理发布，欢迎转载。/g,"");
x(/(\s+)/g,"♾️♾️");
x(/([，\u4e00-\u9fa5])♾️♾️([，\u4e00-\u9fa5])/g,"$1$2");
x(/♾️♾️/g,"\n");
x(/([^！？。]{10}[。？！～][」”’]?)/g,"$1\n");
for(i in n){result=result.replace(m[i],n[i])}

