java.toast("🔥正在刷新列表,请稍等！");
var push = (title, url, size) => result.push({
    "title": title,
    "url": url,
    "style": {
        "layout_flexGrow": 1,
        "layout_flexBasisPercent": size
    }
});

var baseUrl = "http://www.baijiawenxue.com/";
var tag = java.ajax(baseUrl);
if (tag) {
    var doc = org.jsoup.Jsoup.parse(tag);
    var a = doc.select(".nav ul li a");
    push("🔥分类小说🔥", null, 1);
    var count = 0; 
    for (var i in a) {
        if (count === 0) {
            count++;
            continue; 
        }
        var title = a[i].text();
        var url = a[i].attr("href");
        var size = 0.25;
        title = String(title).replace(/\s/g, "");
        url = String(url) + `{{page}}.html`;
        push(title, url, size);
        count++;
    }
} else {
    java.toast("🤔列表刷新失败！！！");
}
JSON.stringify(result);

