loginUrl:
var data = [
    ["视频", "video", "https://v2.api-m.com/api/meinv"],
    ["黑丝", "heisi", "https://v2.api-m.com/api/heisi?return=302"],
    ["白丝", "baisi", "https://v2.api-m.com/api/baisi?return=302"]
];
function get_type() {
    try {
        let varString = source.getVariable();
        java.log("源变量原始数据: " + varString);
        if (!varString) {
            java.toast("当前未设置模式");
            return;
        }
        let currentData = JSON.parse(varString);
        let modeName = "未设置";
        for (let i = 0; i < data.length; i++) {
            if (data[i][1] === currentData.type) {
                modeName = data[i][0];
                break;
            }
        }
        java.toast(`当前模式: ${modeName}`);
    } catch (e) {
        java.toast("模式数据解析错误: " + e.message);
        java.log("解析源变量出错: " + e);
    }
}
function type(mode) {
    try {
        java.log("切换模式: " + mode);
        let validMode = false;
        let modeName = "";
        let apiUrl = "";
        for (let i = 0; i < data.length; i++) {
            if (data[i][1] === mode) {
                validMode = true;
                modeName = data[i][0];
                apiUrl = data[i][2];
                break;
            }
        }
        if (validMode) {
            var variable = {
                "type": mode,
                "value": apiUrl,
                "kind": "hot"
            };
            var jsonStr = JSON.stringify(variable, null, "\t");
            source.setVariable(jsonStr);
            java.toast(`✅ 已切换到: ${modeName}模式`);
            java.log("设置源变量: " + jsonStr);
        } else {
            java.toast("❌ 无效模式");
        }
    } catch (e) {
        java.toast("切换模式出错: " + e.message);
        java.log("切换模式出错: " + e);
    }
}
function init() {
    try {
        let varString = source.getVariable();
        if (!varString) {
            var variable = {
                "type": "video",
                "value": "https://v2.api-m.com/api/meinv",
                "kind": "hot"
            };
            var jsonStr = JSON.stringify(variable, null, "\t");
            source.setVariable(jsonStr);
            java.log("初始化源变量: " + jsonStr);
            java.toast("已初始化默认视频模式");
        } else {
            java.log("已有源变量: " + varString);
        }
    } catch (e) {
        java.log("初始化出错: " + e);
    }
}
init();

