  "js": "java.toast('正在加载详情页，请稍等！');"
}

ruleSearch.coverUrl:
@js:
var url = "{{@@class.bookname@a@href}}";
var id = url.match(/\d+/g);
if (id && id.length > 0) {
	   var firstNumber = id[0];
    var a = firstNumber.slice(0, 2);
} else {
    java.toast('未获取到封面链接');
}
var b = "https://fm.dameishuwang.net/";
var c = "s.jpg";
var d = "/";
var coverUrl = b + a + d + id + d + id + c;
coverUrl

searchUrl:
/modules/article/search.php,{
  "body": "searchkey={{key}}&action=login",
  "charset": "GBK",
  "headers": {
    "user-agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/116.0.0.0 Safari/537.36"
  },
  "js": "java.toast('正在搜索中，请稍等！');",
  "method": "POST",
  "webView": false
}

