exploreUrl:
@js:
sort=[];
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});
push('∇ 个人中心 ∇','',1,1)
push('个人书架',`{{getUrl()}}modules/article/bookcase.php`,1,0.39)
push('我的订阅',`{{getUrl()}}modules/obook/buylist.php`,1,0.39)
push("∇ 全部分类 ∇",null,1,1)
mrList=["全部","玄幻奇幻","都市青春","武侠仙侠","历史军事","女生言情","网游竞技","长篇连载","综合其他","科幻灵异"]
mrList.map((uri,index)=>{
	url=`{{getUrl()}}modules/article/articlefilter.php?order=lastupdate&sortid=${index}&page={{page}}`;
push(uri,url,1,0.25)
});
push("∇ 长篇连载 ∇",null,1,1)
xrList=["都市激情","扒灰乱伦","情色武侠","绿帽情怀","混乱修仙","调教养成"]
xrList.map((uri,index)=>{
	url=`{{getUrl()}}modules/article/articlefilter.php?order=lastupdate&sortid=7&typeid=${index+701}&page={{page}}`;
	push(uri,url,1,0.25)
	});
push("∇ 作品字数 ∇",null,1,1)
arList=["不限","30 万以下","30 - 50 万","50 - 100 万","100 - 200 万","200 万以上"]
arList.map((uri,index)=>{
	url=`{{getUrl()}}modules/article/articlefilter.php?order=lastupdate&words=${index}&page={{page}}`;
	push(uri,url,1,0.35);
	})
	push("∇ 排序方式 ∇",null,1,1)
	srList=[["周点击","weekvisit"],["月点击","monthvisit"],["总点击","allvisit"],["周推荐","weekvote"],["月推荐","monthvote"],["总推荐","allvote"],["周鲜花","weekflower"],["月鲜花","monthflower"],["总鲜花","allflower"],["总月票","allvipvote"],["字数","words"],["收藏数","goodnum"],["更新时间","lastupdate"],["入库时间","postdate"],["编辑推荐","toptime"]]
srList.map([title,uri]=>{
	url=`{{getUrl()}}modules/article/articlefilter.php?order=${uri}&page={{page}}`;
	push(title,url,1,0.25);
	});
		push("∇ 更新时间 ∇",null,1,1)
	drList=["不限","三日内","七日内","半月内","一月内"]
	drList.map((uri,index)=>{
		url=`{{getUrl()}}modules/article/articlefilter.php?order=lastupdate&update=${index}&page={{page}}`;
		push(uri,url,1,0.25)
		});
			push("∇ 写作进度 ∇",null,1,1)
		krList=["不限","新书上传","情节展开","精彩纷呈","接近尾声","已经完本"]
		krList.map((uri,index)=>{
		url=`{{getUrl()}}modules/article/articlefilter.php?isfull=${index}&order=lastupdate&page={{page}}`;		
		push(uri,url,1,0.25)
		});
		push("∇ VIP状态 ∇",null,1,1)
		trList=["不限","VIP作品","免费作品","签约作品"]
		trList.map((uri,index)=>{
			url=`{{getUrl()}}modules/article/articlefilter.php?isvip=${index}&order=lastupdate&page={{page}}`;
			push(uri,url,1,0.25)
			});
			//java.log(JSON.stringify(sort))
JSON.stringify(sort)

