function getLogin() {
    url=getUrl()
    index=getIndex(url)
    username=result.get("用户名")
    psw=result.get("密码")
    ck = java.getCookie(url)
    c = String(ck).indexOf('nick=')
    k = String(ck).indexOf('nick=deleted')
    if(c!=-1&&k==-1){
        java.toast("【" + index + "站】\n" + "\n已登录，请勿重复登录！")
    }else{
        if(username=="" || psw==""){
            java.toast("用户名或密码为空！")
        }else{
            code = java.getVerificationCode(url+ 'captcha/code.php')
            request = JSON.stringify({
                'body': "Username=" + username + "&password=" + psw + "&captcha_code=" + code + "&loginbtm=",
                'method': 'POST'
            })
            java.ajax(url + 'login.php,' + request)
            cckk = java.getCookie(url)
            cc = String(cckk).indexOf('nick=')
            kk = String(cckk).indexOf('nick=deleted')
            if (cc!=-1&&kk==-1) {
                java.toast("⭕【" + index + "站】登录成功！")
            }else if(cc=-1&&kk=-1){
                java.longToast("验证码错误！")
