loginUrl:
var hosts = [
	   "番茄",
    "七猫",
    "猫眼",
    "得间",
];

function sethost(index) {
    if (index >= 1 && index <= hosts.length) {
        source.setVariable(JSON.stringify([{"host": hosts[index-1]}]));
        java.longToast("设置成功，当前搜索：" + hosts[index-1]);
    } else {
        java.toast("❌ 无效的线路索引");
    }
}
function login() {}

function zs(zs) {
java.startBrowserAwait(source.bookSourceUrl+'/static/img/z.png',"喝杯咖啡");
