function hmd(){
	let id = cache.getFromMemory('8xid');
	if (id) {
	let hmd = cache.get('8xhmd')||'{}';
	hmd = JSON.parse(hmd);
	let idd = Object.keys(id)[0];
	if (idd in hmd) {
		delete hmd[idd];
		cache.put('8xhmd',JSON.stringify(hmd),0);
		java.longToast('\n已将书籍：'+Object.values(id)[0]+'\n移除黑名单😅');
		}	else {
	hmd = Object.assign({},hmd,id);
	cache.put('8xhmd',JSON.stringify(hmd),0);
	java.longToast('\n已将书籍：'+Object.values(id)[0]+'\n加入黑名单😤');}

	} else {java.toast('\n请在书籍界面点击或刷新书籍');}

}

function hmdre(){
	let hmd=cache.get('8xhmd')||'{}';
	hmd=JSON.parse(hmd);
	let ss=Object.entries(hmd);
let [cb,cf] = colorz();
let svg=`<svg width="990" height="300" xmlns="http://www.w3.org/2000/svg">
<style>
text {
	white-space: pre;
	word-wrap: break-word;}
</style>
<rect width="100%" height="150%" fill="#${cb}"/>
<text fill="#${cf}" text-anchor="middle" dominant-baseline="middle" stroke="#000000" stroke-width="1" font-weight="bold">
<tspan x="50%" dy="30%" font-size="100">
是否清空黑名单<tspan font-size="30">（${ss.length}）</tspan>
</tspan>
<tspan x="42%" dy="25%" font-size="60">输入“确认”进行清空</tspan>
<tspan x="50%" dy="20%" font-size="50">输入“名单”打印一份名单到日志</tspan>
<tspan x="50%" dy="20%" font-size="50">输入书籍ID单独移出黑名单<tspan font-size="30">(名单查看)</tspan></tspan>
</text>
</svg>`;
svg=java.base64Encode(svg);

try {
//拦截验证码报错
let ysr=java.getVerificationCode('data:image/svg+xml;base64,'+svg);
Packages.java.lang.Thread.sleep(100);
if (ysr=='确认') {
	cache.delete('8xhmd');
	java.toast('\n已清空');
}
else if (ysr=='名单') {
	java.log('\n💔8x黑名单：\n'+ss.join('\n'));
	cache.put('8xbff',source.getLoginInfo());
	cache.put('8xbff2',source.getLoginHeader());
}
else if (/\d{3,}/.test(ysr)) {
	if (ysr in hmd) {
	java.toast('\n已将书籍：'+hmd[ysr]+'\n移除黑名单');
	delete hmd[ysr];
	cache.put('8xhmd',JSON.stringify(hmd),0);
	} else {java.toast('移出失败');}
}
else if (ysr=='恢复') {
	let ryr=cache.get('8xbff');
	let ryr2=cache.get('8xbff2');
	source.putLoginInfo(ryr);
	source.putLoginHeader(ryr2);
}

} catch(err) {}
}

