b64 = java['base64Encode'](`
    <html>
    <head>
        <meta name="viewport" content="width=device-width">
        <style>
            body {
                margin: 0;
                font-family: Arial, sans-serif;
                background: linear-gradient(135deg, #ece9e6, #ffffff);
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: flex-start;
                padding: 20px;
            }
            .container {
                width: 100%;
                max-width: 800px;
                background: #fff;
                border-radius: 10px;
                box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                overflow: hidden;
                margin-bottom: 20px;
            }
            h3 {
                margin: 0;
                padding: 10px 20px;
                font-size: 0.9em;
                background: #333;
                color: #fff;
                border-radius: 10px 10px 0 0;
            }
            .video-container {
                width: 100%;
                background: #000;
                position: relative;
            }
            video {
                width: 100%;
                height: auto;
                display: block;
            }
            .info-container {
                padding: 20px;
            }
            .info-container p {
                margin: 10px 0;
                color: #555;
            }
            .info-container p span {
                font-weight: bold;
                color: #000;
            }
        </style>
    </head>
    <body>
        <div class="container">
            <h3>{{book.name}}</h3>
            <div class="video-container">
                <video id="video" src="${url}" poster="{{book.coverUrl}}" controls></video>
            </div>
        </div>
        <div class="info-container">
              <p>üßæ ÂêçÁß∞Ôºö<span>{{book.name}}</span></p>
             <p>üßæ ÂàÜÁ±ªÔºö<span>{{book.author}}</span></p>
             <p>üîñ Ê†áÁ≠æÔºö<span>{{book.kind}}</span></p>
            <p>üè∑ ÁÆÄ‰ªãÔºö<span>{{book.intro}}</span></p>
        </div>
        <script>
            // Ëé∑ÂèñËßÜÈ¢ëÂÖÉÁ¥†
            const video = document.getElementById('video');
            let touchStartX = 0;
            let touchStartY = 0;
            let touchStartTime = 0;
            let isLongPress = false;

            // ÊªëÂä®Âø´Ëøõ
            video.addEventListener('touchstart', (e) => {
                touchStartX = e.touches[0].clientX;
                touchStartY = e.touches[0].clientY;
                touchStartTime = Date.now();
                isLongPress = false;
            });

            video.addEventListener('touchmove', (e) => {
                const deltaX = e.touches[0].clientX - touchStartX;
                const deltaY = e.touches[0].clientY - touchStartY;

                // Â¶ÇÊûúÊòØÊ∞¥Âπ≥ÊªëÂä®Ôºå‰∏îÊú™ËøõÂÖ•ÈïøÊåâÁä∂ÊÄÅ
                if (Math.abs(deltaX) > Math.abs(deltaY) && !isLongPress) {
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 50); // ÊØèÊªëÂä®100pxÂø´Ëøõ1Áßí
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                }
                e.preventDefault(); // Èò≤Ê≠¢ÈªòËÆ§Ë°å‰∏∫ÔºàÂ¶ÇÈ°µÈù¢ÊªöÂä®Ôºâ
            });

            // ÈïøÊåâÂø´Ëøõ
            video.addEventListener('touchend', (e) => {
                const touchEndTime = Date.now();
                const touchDuration = touchEndTime - touchStartTime;

                // Â¶ÇÊûúÈïøÊåâÊó∂Èó¥Ë∂ÖËøá500msÔºåËøõÂÖ•ÈïøÊåâÂø´ËøõÁä∂ÊÄÅ
                if (touchDuration > 500) {
                    isLongPress = true;

                    // Âú®ÈïøÊåâÁä∂ÊÄÅ‰∏ãÔºåÊ†πÊçÆÊªëÂä®Ë∑ùÁ¶ªÂø´Ëøõ
                    const deltaX = e.changedTouches[0].clientX - touchStartX;
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 100); // ÊØèÊªëÂä®100pxÂø´Ëøõ1Áßí
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                } else {
                    isLongPress = false;
                }
            });
        </script>
    </body>
    </html>
`);
