	   var books=getbook[0];
    books.is_bookshelf = false;
    //data.push(books);
	}
if (data.length) {
	var option = {
"method": "POST",
"body": JSON.stringify(data),
};
    java.ajax(`${base_url}/push_user_bookshelf,`+JSON.stringify(option));
    }
	} catch (e) {}
	result;
</js>

