function login(b) {
  if (b == undefined) return true;
  info = result
  so = getUrl();
  pw = info.密码

  if (b) {
    url = so + 'member.php?mod=logging&action=login&loginsubmit=yes&loginhash=LPfez&mobile=2&handlekey=loginform&inajax=1,'
    body = `fastloginfield=username&username=${info['用户名/邮箱']}&password=${pw}&questionid=0&answer=&fromuid=`}
  post = JSON.stringify({
    "body": String(body),
    "method": "POST"
  })
  url += post
  result = java.ajax(url)
  //java.log(result)
  //java.toast(result)
  if(result.match(/欢迎您回来/)){
  	name=result.match(/\{\'username'\:\'(.*?)\',\'usergroup\'\:\'(.*?)\'\,\'uid\'\:\'(.*?)\'\}/);
   java.log(name) 
   java.toast(`\n${name[1]} ${name[2]}` + ' \n登录成功');
   }
  c = cookie.getCookie(getUrl())
  source.putLoginHeader(c)
}
