        if(originalOrder) {
            return originalOrder[result.indexOf(a)] - originalOrder[result.indexOf(b)];
        }
        for(var attr of orderAttrs) {
            let va = parseInt(a.attr(attr));
            let vb = parseInt(b.attr(attr));
            if (!isNaN(va) && !isNaN(vb)) return va - vb;
        }
        return 0;
    });

