function set_media(media) {
        let source = getArgument('source');
     if (source!='番茄' && source!='全部'){
                       if (source=='喜马拉雅') {
                           setArgument('media', '听书');
          java.toast("\n\n目前"+source+"：仅支持听书模式！");
                           }else if (source=='七猫' && media == '听书') {
                               setArgument('media', '听书');
            java.toast("\n\n已切换至：听书\n请重新搜索书籍！");
                               } else if (source=='歪瑞古德') {
                               setArgument('media', '漫画');
            java.toast("\n\n目前"+source+"：仅支持漫画模式！");
                               } else {
                                   setArgument('media', '小说');
                                   if (media=='小说') {
                                       java.toast("\n\n已切换至：" + media + '\n请重新搜索书籍！');
                                       } else {
                                           java.toast("\n\n目前"+source+"：不支持"+media+"模式！");
                                           }
                                   }
                                    } else {
                      setArgument('media', media);
         java.toast("\n\n已切换至：" + media + '\n请重新搜索书籍！');
                      }
    }

//获取搜索媒体
function get_media() {
    let media = getArgument('media');
    if (media == '') {
        media = '全部';
    }
    java.toast("\n\n当前搜索范围是：" + media);

}

//获取服务器
function get_server() {
    var server = getArgument('server');
    var tishi = '\n\n当前服务器：' + server
    java.toast(tishi);
    return server
}


//设置服务器
function set_server() {
    let zdyserver;
    try {
        zdyserver = String(source.getLoginInfoMap()['自定义服务器(可不填)']);
        if (zdyserver.includes('http')) {
            setArgument('server', zdyserver);
            java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
        } else {
            zdyserver = '';
        }
    } catch (error) {
        zdyserver = '';
    }
    if (!zdyserver) {
        const servers = String(source.bookSourceComment).match(/https?:\/\/[^\s]+/g);
        const currentServer = getArgument('server') || '';
        const currentIndex = servers.indexOf(currentServer);

        const nextIndex = currentIndex >= 0 ? (currentIndex + 1) % servers.length : 0;
        const nextServer = servers[nextIndex];

        setArgument('server', nextServer);
        java.toast(`\n\n当前服务器${nextIndex+1}:${nextServer}\n\n注意：切换服务器后需要在详情页刷新目录才能生效`);
    }
}

//获取音色
var tone_id = getArgument('tone_id');

function get_tone_id(arg) {
    var datadist = {
        "0": "默认音色",
        "-1": "阅读模式",
        "-2": "漫画模式",
        "51": "多人发音",
        "1": "甜美少女",
        "2": "清亮青叔",
        "5": "开朗青年",
        "6": "温柔淑女",
        "4": "成熟大叔",
        "74": "大叔升级",
        "30": "优雅御姐"
    };
    var tone_id = datadist[arg] || arg;
    var tishi = '\n\n当前音色：' + tone_id;
    java.toast(tishi);
}

//设置音色
function set_tone_id(mode, name) {
    let zdytone_id;
    try {
        zdytone_id = String(source.getLoginInfoMap()['其他音色填写后点击右上角✔']);
        if (zdytone_id) {
            setArgument('tone_id', zdytone_id);
            java.toast(`\n\n当前音色为自定义音色\n${zdytone_id}\n\n切换音色请先清空音色输入框中的数据`);
        } else {
            zdytone_id = '';
        }
    } catch (error) {
        zdytone_id = '';
    }
    if (!zdytone_id) {
        let toast = "\n\n已切换至：" + name + '\n\n切换后需要刷新详情页';
        setArgument('tone_id', mode);
        java.toast(toast);
    }
}

//设置来源
function set_source(source) {
	          setArgument('source', source);
	          set_media('小说');
            java.toast(`\n\n当前来源已切换为:\n${source}\n\n切换后请重新搜索`);
       }

