function jinghuafm() {
const {java, source} = this;	
function splitString(str) {
    const length = str.length;
    if (length < 15) {
        return length<9 ? [str] : [
            str.slice(0, 7),
            str.slice(7)
        ].filter(s => s);
    } else if (length < 20) {
        return [
            str.slice(0, 7),
            str.slice(7,13),
            str.slice(13)
        ].filter(s => s);
    }  else if (length < 24) {
        return [
            str.slice(0, 7),
            str.slice(7,13),
            str.slice(13,18),
            str.slice(18)
        ].filter(s => s);
    } else {
        return [
            str.slice(0, 7),
            str.slice(7, 13),
            str.slice(13, 18),
            str.slice(18, 22),
            str.slice(22)
        ].filter(s => s);
    }
}
function pingString(ss) {
let rr='';
let l1=ss.length;
for (let i1=0; i1<l1; i1++) {
	let c1=ss[i1], l2=c1.length;
	for (let i2=0; i2<l2; i2++) {
		let c2=c1.charAt(i2);
		rr += `<tspan x="${100+180*ii}" y="${180+170*(i2+i1)}">${c2}</tspan>`;
		}
		ii++;
	}
	return rr;
}

