        let key = String(cookie.getCookie(server)) || String(java.getCookie(server));
        if (key) {
            let keyParts = key.split(';')
            for (let key of keyParts) {
                  if (key.includes("secretKey=")) {
                  keyParts = key.split('secretKey=');
