var Importer = new JavaImporter(
    Packages.java.net,
    Packages.java.io,
    Packages.java.util,
    Packages.java.lang,
    Packages.java.util.concurrent
);

var hosts = [
    "manhuafree.com",
    "m.g-mh.org",
    "baozimh.org",
    "bzmh.org"

];

function getTimestamp() {
    return Packages.java.lang.System.currentTimeMillis();
}

function checkLatency(urlString) {
    var connection = null;
    try {
        if (!urlString.startsWith("http://") && !urlString.startsWith("https://")) {
            urlString = "https://" + urlString;
        }
        var startTime = getTimestamp();
        var url = new Packages.java.net.URL(urlString);
        connection = url.openConnection();
        connection.setRequestMethod("GET");
        connection.setConnectTimeout(3000);
        connection.setReadTimeout(3000);

        var inputStream = connection.getInputStream();
        inputStream.close();

        var latency = getTimestamp() - startTime;
        java.log("[SUCCESS] " + urlString + " | " + latency + "ms");
        return latency;
    } catch (e) {
        java.log("[FAILED] " + urlString + " | " + e);
        return null;
    } finally {
        try {
            if (connection) connection.disconnect();
        } catch (e) {}
    }
}

function checkHostsLatency(hosts) {
    with (Importer) {
        try {
            var totalStart = getTimestamp();
            java.longToast("🚀 启动检测");

            var results = [];
            var total = hosts.length;

            for (var i = 0; i < hosts.length; i++) {
                var host = hosts[i];
                // 更新进度提示
                java.longToast("🔍 检测中 (" + (i+1) + "/" + total + ")");

                var latency = checkLatency(host);
                if (latency !== null) {
                    results.push({
                        host: host,
                        latency: latency
                    });
                }
            }

            var totalTime = getTimestamp() - totalStart;
            java.log("总耗时: " + totalTime + "ms");

            if (results.length === 0) {
                java.toast("❌ 无可用主机");
            } else {
                results.sort(function(a, b) {
                    return a.latency - b.latency;
                });

                var best = results[0];
                var summary = "✅ 完成检测\n最佳: " + best.host + "\n⏱️ " + best.latency + "ms";
                java.longToast(summary);
                let ms = "共找到 " + results.length + "/" + total + " 可用节点"
                java.longToast(ms);
                java.log(ms);
                source.setVariable(JSON.stringify(results, null, 2));
            }
        } catch (e) {
            java.log("[FATAL] " + e);
        }
    }
}

