cat=String(J.select('a[style="text-decoration:none;color: red;"]').text()).replace(/\s/g,',');
status=J.select('span[itemprop="updataStatus"]').text();
size=String(J.select('span[itemprop="wordCount"]').text()).replace(/字/,'');
n=String(java.getElement('@@id.oneboolt@tag.tr.-2@span').text()).replace(/\n/,'•').replace(/\*最新更新/,'').replace(/(.*?)\[VIP\]/,'🔒$1');
num=java.getElement('@@id.oneboolt@tag.tr.-2@tag.td.0').text();
