json.push({
title:title,
author:author,
cat:view+","+status+","+cat+','+genre,
size:size,
url:"http://app-cdn.jjwxc.net/androidapi/novelbasicinfo?novelId="+id,
des:des,
new:String(num+"、"+n).replace(/(\d+、)🔒/,'🔒$1').replace(/^章节、|、$/,''),
