// 读取并返回数据
function checkData(_) {
	const { java, source, cache }= _ || this
	java.log("checkData()")
	const vName = `sourceVariable_${source.getKey()}`
	let temp
	try{
		temp = []
		// 尝试从缓存中分段读取数据
		for(let i = 0; i < cache.get(`${vName}_iLength`);){
			temp.push(cache.get(`${vName}_${i}`))
			i += 500000
		}
		temp = temp.join("")
		// 如果没有数据，尝试从缓存文件或源变量中获取
		if (!temp||temp=="") {
			temp=cache.getFile(vName)||cache.get(vName)
		}
		// 解析 JSON 数据
		const $=JSON.parse(temp)
		// 验证数据结构
		if (typeof $.books!='object') {
			throw new Error("错误数据")
		}
		return $
	} catch (e) {
		java.log(`function checkData Error:${e}`)
		return reset(false, false, this)
	}
}
// 保存数据
function setv(json, _, isString) {
	const { java, source, cache }= _ || this
	const vName = `sourceVariable_${source.getKey()}`
	java.log("setv()")
	// 如果输入不是字符串，则将其转换为 JSON 字符串
	if (!isString) {
		json = JSON.stringify(json)
	}
	java.logType(json)
	java.log(`json length: ${json.length}`)
	// 记录数据的长度
	cache.put(`${vName}_iLength`,json.length,0)
	// 分段存储数据
	for (let i = 0; i < json.length;) {
		cache.put(`${vName}_${i}`,json.slice(i, i += 500000),0)
	}
}
// 重置数据
function reset(check, showTip, _) {
	const { java, source, cache }= _ || this
	let vName = String(`sourceVariable_${source.getKey()}`)
	java.log("reset()"+check+showTip)
	if(check){
		try{
			check = java.getVerificationCode(checkImg)
		}catch(e){}
	} else {
		check = "y"
	}
	if (check == "y") {
	let $ = {
		api:2,
		time:20250204,
		lastestId:11759,
		sum:50,
		isLocal:false,
		order:false,
		errorIds:[],
		sortType:"",
		concurrency:1,
		books:{}
		}
	for (i = 0; i < cache.get(`${vName}_iLength`);) {
		cache.delete(`${vName}_${i}`)
		i += 500000
	}
	cache.delete(vName)
	cache.delete(`${vName}_iLength`)
	setv($, _||this)
	if (showTip) {
	java.toast('\n✅重置成功，请重新构造本地数据')
	}
	return $
	} else {
		java.toast("\n❎用户取消重置❎")
	}
}
function getl(arr, page){
	return arr.slice(page*500, (+page+1)*500)
}
function toDataUrl(key, page, sortType) {
	const { java } = this
	let data = [key, page, sortType]
	return `data:;base64,${java.base64Encode(data.join("|==|"))},{"type":""}`
}
function sleep(t) {
    Packages.java.lang.Thread.sleep(t)
}
function createRegExp(input) {
    const flagsRegex = /\(\?([igm]*)\)(?!\))/g;
    let flags = '';
    let pattern = input;
    let match;
    while ((match = flagsRegex.exec(input)) !== null) {
        flags += match[1];
        pattern = pattern.substring(0, match.index) + pattern.substring(match.index + match[0].length);
    }
    return new RegExp(pattern, flags);
}
function getSortValue(book, type) {
	let data = book[type]
	let value
	switch (type) {
		case 'pornrate':
			value = data.split("%")[0]
			break;
		case 'update':
			value = parseInt(data.replace(/-/g,""))
			break;
		case 'word':
			m = data.match(/[^\d.]+/)
			f = 1
			if (m&&/k/i.test(m)) {
				f = 1000
			}else if(m&&/m/i.test(m)) {
				f = 1000000
			}
			value = Number(data.match(/[\d.]+/))*f
			break;
		default:
			value = data
	}
	return value
}
function spArr(arr, num) {
	let newArr = []
	for(let i = 0; i < arr.length;){
		newArr.push(arr.slice(i, i += num))
	}
	return newArr
}
