@js:
if (baseUrl.includes('/watchlist/novel') ) {
	rrr = '';
	tm = java.getString('$.last_published_content_datetime');
	} else {
		let tags = java.getStringList('$.tags..name');
		//将list数组处理为js正常数组，并且保证了元素值为js字符串
		rr=String(tags).slice(1,-1).split(', ');
		//下面方式会复杂了点，但能保证元素也能含逗号
		//rr = Array.from(tags);rr=rr.join().split(',');
		rrr = rr.filter(i => biaoq.test(i) );
		rr.forEach(x=>{
			if (x=='萝莉') {
				if (!rrr.includes('幼女')) rrr.unshift(x);}
			else if (['熟女','淫妻'].includes(x)) {
				if (!rrr.includes('人妻')) rrr.unshift(x);}
			else if (['仙侠','古风','武侠'].includes(x) && !rrr.some(y=>/[修仙古风武侠]{2}/.test(y))) {
					rrr.unshift(x);}


	});
		tm = java.getString('$.create_date');
}
let d=new Date(tm);
d=java.timeFormatUTC(d,'yy/MM/dd',8*60*60*1000);

rrr.toString()+','+d

